import { createStore } from 'vuex';

export default createStore({
  state: {
    todos: []
  },
  mutations: {
    addTodo(state, newTodo) {
      // newTodo.text에 대해 trim을 사용합니다.
      if (newTodo.text.trim()) {
        state.todos.push({ text: newTodo.text.trim(), done: false, date: newTodo.date });
      }
    },
    removeTodo(state, index) {
      state.todos.splice(index, 1);
    },
    toggleTodoStatus(state, index) {
      state.todos[index].done = !state.todos[index].done;
    }
  },
  actions: {
    addTodo({ commit }, newTodo) {
      commit('addTodo', newTodo);  // 새로운 할 일 객체를 넘겨줌
    },
    removeTodo({ commit }, index) {
      commit('removeTodo', index);
    },
    toggleTodoStatus({ commit }, index) {
      commit('toggleTodoStatus', index);
    }
  },
  getters: {
    todos: (state) => state.todos
  }
});
